import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { geminiService } from "./services/gemini";
import { liveKitService } from "./services/livekit";
import { insertInterviewSchema, insertUserPreferencesSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUserWithPreferences(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User preferences routes
  app.get('/api/preferences', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const preferences = await storage.getUserPreferences(userId);
      res.json(preferences);
    } catch (error) {
      console.error("Error fetching preferences:", error);
      res.status(500).json({ message: "Failed to fetch preferences" });
    }
  });

  app.post('/api/preferences', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const validatedData = insertUserPreferencesSchema.parse(req.body);
      const preferences = await storage.upsertUserPreferences(userId, validatedData);
      res.json(preferences);
    } catch (error) {
      console.error("Error updating preferences:", error);
      res.status(500).json({ message: "Failed to update preferences" });
    }
  });

  // Interview routes
  app.get('/api/interviews', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const interviews = await storage.getUserInterviews(userId);
      res.json(interviews);
    } catch (error) {
      console.error("Error fetching interviews:", error);
      res.status(500).json({ message: "Failed to fetch interviews" });
    }
  });

  app.post('/api/interviews', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const validatedData = insertInterviewSchema.parse(req.body);

      // Create interview
      const interview = await storage.createInterview(userId, validatedData);

      // Generate questions using Gemini
      const questions = await geminiService.generateInterviewQuestions({
        jobRole: interview.jobRole,
        experienceLevel: interview.experienceLevel,
        techStack: interview.techStack || [],
        questionCount: interview.totalQuestions || 8,
      });

      // Save questions to database
      for (let i = 0; i < questions.length; i++) {
        await storage.addQuestionToInterview(interview.id, {
          questionText: questions[i].question,
          questionType: questions[i].type,
          orderIndex: i,
        });
      }

      // Create LiveKit room if voice enabled
      if (interview.voiceEnabled) {
        const roomName = await liveKitService.createInterviewRoom(interview.id);
        await storage.updateInterview(interview.id, { roomName });
      }

      const fullInterview = await storage.getInterviewWithQuestions(interview.id);
      res.json(fullInterview);
    } catch (error) {
      console.error("Error creating interview:", error);
      res.status(500).json({ message: "Failed to create interview" });
    }
  });

  app.get('/api/interviews/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const interview = await storage.getInterviewWithQuestions(req.params.id);

      if (!interview || interview.userId !== userId) {
        return res.status(404).json({ message: "Interview not found" });
      }

      res.json(interview);
    } catch (error) {
      console.error("Error fetching interview:", error);
      res.status(500).json({ message: "Failed to fetch interview" });
    }
  });

  app.patch('/api/interviews/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const interview = await storage.getInterview(req.params.id);

      if (!interview || interview.userId !== userId) {
        return res.status(404).json({ message: "Interview not found" });
      }

      const updated = await storage.updateInterview(req.params.id, req.body);
      res.json(updated);
    } catch (error) {
      console.error("Error updating interview:", error);
      res.status(500).json({ message: "Failed to update interview" });
    }
  });

  app.delete('/api/interviews/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const interview = await storage.getInterview(req.params.id);

      if (!interview || interview.userId !== userId) {
        return res.status(404).json({ message: "Interview not found" });
      }

      // End LiveKit room if exists
      if (interview.roomName) {
        await liveKitService.endInterviewRoom(interview.roomName);
      }

      const deleted = await storage.deleteInterview(req.params.id);
      res.json({ success: deleted });
    } catch (error) {
      console.error("Error deleting interview:", error);
      res.status(500).json({ message: "Failed to delete interview" });
    }
  });

  // Question response routes
  app.post('/api/questions/:id/response', isAuthenticated, async (req: any, res) => {
    try {
      const { response, transcript, timeSpent } = req.body;

      if (!response) {
        return res.status(400).json({ message: "Response is required" });
      }

      // Get question and interview details for evaluation
      const question = await storage.updateQuestionResponse(
        req.params.id,
        response,
        transcript,
        undefined, // score will be added after evaluation
        undefined, // feedback will be added after evaluation
        timeSpent
      );

      if (!question) {
        return res.status(404).json({ message: "Question not found" });
      }

      // Get interview for context
      const interview = await storage.getInterview(question.interviewId);
      if (!interview) {
        return res.status(404).json({ message: "Interview not found" });
      }

      // Evaluate response using Gemini
      const evaluation = await geminiService.evaluateResponse({
        question: question.questionText,
        response,
        jobRole: interview.jobRole,
        experienceLevel: interview.experienceLevel,
      });

      // Update question with evaluation results
      const updatedQuestion = await storage.updateQuestionResponse(
        req.params.id,
        response,
        transcript,
        evaluation.score,
        evaluation.feedback,
        timeSpent
      );

      res.json({
        question: updatedQuestion,
        evaluation,
      });
    } catch (error) {
      console.error("Error submitting response:", error);
      res.status(500).json({ message: "Failed to submit response" });
    }
  });

  // LiveKit token generation
  app.post('/api/interviews/:id/token', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const interview = await storage.getInterview(req.params.id);

      if (!interview || interview.userId !== userId) {
        return res.status(404).json({ message: "Interview not found" });
      }

      if (!interview.roomName) {
        return res.status(400).json({ message: "Interview does not have voice enabled" });
      }

      const user = await storage.getUser(userId);
      const participantName = user?.firstName || user?.email || `User-${userId}`;

      const token = await liveKitService.generateAccessToken(
        interview.roomName,
        participantName,
        userId
      );

      res.json({ 
        token, 
        wsUrl: process.env.LIVEKIT_URL || 'wss://your-livekit-instance.livekit.cloud',
        roomName: interview.roomName
      });
    } catch (error) {
      console.error("Error generating LiveKit token:", error);
      res.status(500).json({ message: "Failed to generate access token" });
    }
  });

  // Analytics routes
  app.get('/api/analytics/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const stats = await storage.getUserStats(userId);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching stats:", error);
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

  // Interview completion route
  app.post('/api/interviews/:id/complete', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const interview = await storage.getInterviewWithQuestions(req.params.id);

      if (!interview || interview.userId !== userId) {
        return res.status(404).json({ message: "Interview not found" });
      }

      // Calculate overall scores
      const questionScores = interview.questions
        .filter(q => q.responseScore)
        .map(q => parseFloat(q.responseScore!));

      const communicationScores = interview.questions
        .filter(q => q.responseScore) // Simplified for now
        .map(q => parseFloat(q.responseScore!) * 0.8); // Communication component

      const technicalScores = interview.questions
        .filter(q => q.questionType === 'technical' && q.responseScore)
        .map(q => parseFloat(q.responseScore!));

      const overallScore = questionScores.length > 0
        ? questionScores.reduce((a, b) => a + b, 0) / questionScores.length
        : 0;

      const communicationScore = communicationScores.length > 0
        ? communicationScores.reduce((a, b) => a + b, 0) / communicationScores.length
        : 0;

      const technicalScore = technicalScores.length > 0
        ? technicalScores.reduce((a, b) => a + b, 0) / technicalScores.length
        : overallScore;

      // Generate overall feedback
      const evaluations = interview.questions
        .filter(q => q.responseScore && q.responseFeedback)
        .map(q => ({
          score: parseFloat(q.responseScore!),
          feedback: q.responseFeedback!,
          strengths: [],
          improvements: [],
          confidenceLevel: 75, // Simplified
          communicationScore: communicationScore,
          technicalScore: technicalScore,
        }));

      const overallFeedback = evaluations.length > 0
        ? await geminiService.generateOverallFeedback(evaluations)
        : "Complete more questions to receive detailed feedback.";

      // Update interview with final scores
      const completedInterview = await storage.updateInterview(req.params.id, {
        status: 'completed' as any,
        overallScore: overallScore.toString() as any,
        communicationScore: communicationScore.toString() as any,
        technicalScore: technicalScore.toString() as any,
        confidenceLevel: '80', // Simplified calculation
        feedback: overallFeedback,
        duration: req.body.duration || 30,
      });

      // End LiveKit room
      if (interview.roomName) {
        await liveKitService.endInterviewRoom(interview.roomName);
      }

      res.json(completedInterview);
    } catch (error) {
      console.error("Error completing interview:", error);
      res.status(500).json({ message: "Failed to complete interview" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  interviews,
  interviewQuestions,
  userPreferences,
  type User,
  type UpsertUser,
  type Interview,
  type InsertInterview,
  type InterviewQuestion,
  type InsertQuestion,
  type UserPreferences,
  type InsertUserPreferences,
  type InterviewWithQuestions,
  type UserWithPreferences,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, count } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  getUserWithPreferences(id: string): Promise<UserWithPreferences | undefined>;

  // Interview operations
  createInterview(userId: string, interview: InsertInterview): Promise<Interview>;
  getInterview(id: string): Promise<Interview | undefined>;
  getInterviewWithQuestions(id: string): Promise<InterviewWithQuestions | undefined>;
  getUserInterviews(userId: string): Promise<Interview[]>;
  updateInterview(id: string, updates: Partial<Interview>): Promise<Interview | undefined>;
  deleteInterview(id: string): Promise<boolean>;

  // Question operations
  addQuestionToInterview(interviewId: string, question: InsertQuestion): Promise<InterviewQuestion>;
  updateQuestionResponse(
    questionId: string, 
    response: string, 
    transcript?: string, 
    score?: number, 
    feedback?: string,
    timeSpent?: number
  ): Promise<InterviewQuestion | undefined>;

  // User preferences operations
  getUserPreferences(userId: string): Promise<UserPreferences | undefined>;
  upsertUserPreferences(userId: string, preferences: InsertUserPreferences): Promise<UserPreferences>;

  // Analytics operations
  getUserStats(userId: string): Promise<{
    totalInterviews: number;
    averageScore: number;
    confidenceLevel: number;
    practiceTime: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async getUserWithPreferences(id: string): Promise<UserWithPreferences | undefined> {
    const [result] = await db
      .select()
      .from(users)
      .leftJoin(userPreferences, eq(users.id, userPreferences.userId))
      .where(eq(users.id, id));

    if (!result.users) return undefined;

    return {
      ...result.users,
      preferences: result.user_preferences || undefined,
    };
  }

  // Interview operations
  async createInterview(userId: string, interview: InsertInterview): Promise<Interview> {
    const [newInterview] = await db
      .insert(interviews)
      .values({
        ...interview,
        userId,
      })
      .returning();
    return newInterview;
  }

  async getInterview(id: string): Promise<Interview | undefined> {
    const [interview] = await db
      .select()
      .from(interviews)
      .where(eq(interviews.id, id));
    return interview;
  }

  async getInterviewWithQuestions(id: string): Promise<InterviewWithQuestions | undefined> {
    const [interview] = await db
      .select()
      .from(interviews)
      .where(eq(interviews.id, id));

    if (!interview) return undefined;

    const questions = await db
      .select()
      .from(interviewQuestions)
      .where(eq(interviewQuestions.interviewId, id))
      .orderBy(interviewQuestions.orderIndex);

    return {
      ...interview,
      questions,
    };
  }

  async getUserInterviews(userId: string): Promise<Interview[]> {
    return await db
      .select()
      .from(interviews)
      .where(eq(interviews.userId, userId))
      .orderBy(desc(interviews.createdAt));
  }

  async updateInterview(id: string, updates: Partial<Interview>): Promise<Interview | undefined> {
    const [updated] = await db
      .update(interviews)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(interviews.id, id))
      .returning();
    return updated;
  }

  async deleteInterview(id: string): Promise<boolean> {
    const result = await db
      .delete(interviews)
      .where(eq(interviews.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  // Question operations
  async addQuestionToInterview(interviewId: string, question: InsertQuestion): Promise<InterviewQuestion> {
    const [newQuestion] = await db
      .insert(interviewQuestions)
      .values({
        ...question,
        interviewId,
      })
      .returning();
    return newQuestion;
  }

  async updateQuestionResponse(
    questionId: string,
    response: string,
    transcript?: string,
    score?: number,
    feedback?: string,
    timeSpent?: number
  ): Promise<InterviewQuestion | undefined> {
    const updates: Partial<InterviewQuestion> = {
      userResponse: response,
    };

    if (transcript) updates.responseTranscript = transcript;
    if (score !== undefined) updates.responseScore = score.toString() as any;
    if (feedback) updates.responseFeedback = feedback;
    if (timeSpent !== undefined) updates.timeSpent = timeSpent;

    const [updated] = await db
      .update(interviewQuestions)
      .set(updates)
      .where(eq(interviewQuestions.id, questionId))
      .returning();
    return updated;
  }

  // User preferences operations
  async getUserPreferences(userId: string): Promise<UserPreferences | undefined> {
    const [preferences] = await db
      .select()
      .from(userPreferences)
      .where(eq(userPreferences.userId, userId));
    return preferences;
  }

  async upsertUserPreferences(userId: string, preferences: InsertUserPreferences): Promise<UserPreferences> {
    const [upserted] = await db
      .insert(userPreferences)
      .values({
        ...preferences,
        userId,
      })
      .onConflictDoUpdate({
        target: userPreferences.userId,
        set: {
          ...preferences,
          updatedAt: new Date(),
        },
      })
      .returning();
    return upserted;
  }

  // Analytics operations
  async getUserStats(userId: string): Promise<{
    totalInterviews: number;
    averageScore: number;
    confidenceLevel: number;
    practiceTime: number;
  }> {
    // Get total interviews count
    const [interviewCount] = await db
      .select({ count: count() })
      .from(interviews)
      .where(and(
        eq(interviews.userId, userId),
        eq(interviews.status, 'completed')
      ));

    // Get completed interviews for calculations
    const completedInterviews = await db
      .select()
      .from(interviews)
      .where(and(
        eq(interviews.userId, userId),
        eq(interviews.status, 'completed')
      ));

    const totalInterviews = interviewCount.count || 0;
    let averageScore = 0;
    let confidenceLevel = 0;
    let practiceTime = 0;

    if (completedInterviews.length > 0) {
      const scores = completedInterviews
        .filter(i => i.overallScore)
        .map(i => parseFloat(i.overallScore!));

      const confidenceLevels = completedInterviews
        .filter(i => i.confidenceLevel)
        .map(i => parseFloat(i.confidenceLevel!));

      const durations = completedInterviews
        .filter(i => i.duration)
        .map(i => i.duration!);

      averageScore = scores.length > 0 
        ? scores.reduce((a, b) => a + b, 0) / scores.length 
        : 0;

      confidenceLevel = confidenceLevels.length > 0
        ? confidenceLevels.reduce((a, b) => a + b, 0) / confidenceLevels.length
        : 0;

      practiceTime = durations.reduce((a, b) => a + b, 0);
    }

    return {
      totalInterviews,
      averageScore: Math.round(averageScore * 100) / 100,
      confidenceLevel: Math.round(confidenceLevel * 100) / 100,
      practiceTime: Math.round(practiceTime / 60), // Convert to hours
    };
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  index,
  jsonb,
  pgTable,
  timestamp,
  varchar,
  text,
  integer,
  decimal,
  boolean,
  pgEnum,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { relations } from "drizzle-orm";

// Session storage table.
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table.
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Job roles enum
export const jobRoleEnum = pgEnum('job_role', [
  'frontend_developer',
  'backend_developer',
  'fullstack_developer',
  'mobile_developer',
  'data_scientist',
  'product_manager',
  'ux_designer',
  'devops_engineer',
  'qa_engineer',
  'software_architect',
]);

// Experience level enum
export const experienceLevelEnum = pgEnum('experience_level', [
  'entry',
  'junior',
  'mid',
  'senior',
  'lead',
  'principal',
]);

// Interview status enum
export const interviewStatusEnum = pgEnum('interview_status', [
  'pending',
  'in_progress',
  'completed',
  'cancelled',
]);

// Interviews table
export const interviews = pgTable("interviews", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
  title: varchar("title").notNull(),
  jobRole: jobRoleEnum("job_role").notNull(),
  experienceLevel: experienceLevelEnum("experience_level").notNull(),
  techStack: text("tech_stack").array(),
  status: interviewStatusEnum("status").default('pending'),
  duration: integer("duration"), // in minutes
  totalQuestions: integer("total_questions").default(8),
  currentQuestion: integer("current_question").default(0),
  overallScore: decimal("overall_score", { precision: 3, scale: 2 }),
  confidenceLevel: decimal("confidence_level", { precision: 3, scale: 2 }),
  communicationScore: decimal("communication_score", { precision: 3, scale: 2 }),
  technicalScore: decimal("technical_score", { precision: 3, scale: 2 }),
  feedback: text("feedback"),
  voiceEnabled: boolean("voice_enabled").default(false),
  roomName: varchar("room_name"), // LiveKit room identifier
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Interview questions table
export const interviewQuestions = pgTable("interview_questions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  interviewId: varchar("interview_id").notNull().references(() => interviews.id, { onDelete: 'cascade' }),
  questionText: text("question_text").notNull(),
  questionType: varchar("question_type").notNull(), // 'technical', 'behavioral', 'situational'
  orderIndex: integer("order_index").notNull(),
  userResponse: text("user_response"),
  responseTranscript: text("response_transcript"),
  responseScore: decimal("response_score", { precision: 3, scale: 2 }),
  responseFeedback: text("response_feedback"),
  timeSpent: integer("time_spent"), // in seconds
  createdAt: timestamp("created_at").defaultNow(),
});

// User preferences table
export const userPreferences = pgTable("user_preferences", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
  preferredJobRole: jobRoleEnum("preferred_job_role"),
  preferredExperienceLevel: experienceLevelEnum("preferred_experience_level"),
  preferredTechStack: text("preferred_tech_stack").array(),
  voiceEnabledByDefault: boolean("voice_enabled_by_default").default(true),
  darkMode: boolean("dark_mode").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Relations
export const usersRelations = relations(users, ({ many, one }) => ({
  interviews: many(interviews),
  preferences: one(userPreferences),
}));

export const interviewsRelations = relations(interviews, ({ one, many }) => ({
  user: one(users, {
    fields: [interviews.userId],
    references: [users.id],
  }),
  questions: many(interviewQuestions),
}));

export const interviewQuestionsRelations = relations(interviewQuestions, ({ one }) => ({
  interview: one(interviews, {
    fields: [interviewQuestions.interviewId],
    references: [interviews.id],
  }),
}));

export const userPreferencesRelations = relations(userPreferences, ({ one }) => ({
  user: one(users, {
    fields: [userPreferences.userId],
    references: [users.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).pick({
  id: true,
  email: true,
  firstName: true,
  lastName: true,
  profileImageUrl: true,
});

export const insertInterviewSchema = createInsertSchema(interviews).pick({
  title: true,
  jobRole: true,
  experienceLevel: true,
  techStack: true,
  voiceEnabled: true,
});

export const insertQuestionSchema = createInsertSchema(interviewQuestions).pick({
  questionText: true,
  questionType: true,
  orderIndex: true,
});

export const insertUserPreferencesSchema = createInsertSchema(userPreferences).pick({
  preferredJobRole: true,
  preferredExperienceLevel: true,
  preferredTechStack: true,
  voiceEnabledByDefault: true,
  darkMode: true,
});

// Types
export type UpsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertInterview = z.infer<typeof insertInterviewSchema>;
export type Interview = typeof interviews.$inferSelect;
export type InsertQuestion = z.infer<typeof insertQuestionSchema>;
export type InterviewQuestion = typeof interviewQuestions.$inferSelect;
export type InsertUserPreferences = z.infer<typeof insertUserPreferencesSchema>;
export type UserPreferences = typeof userPreferences.$inferSelect;

// Extended types with relations
export type InterviewWithQuestions = Interview & {
  questions: InterviewQuestion[];
};

export type UserWithPreferences = User & {
  preferences?: UserPreferences;
};

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/theme-provider";
import { useAuth } from "@/hooks/useAuth";
import Landing from "@/pages/landing";
import Home from "@/pages/home";
import Dashboard from "@/pages/dashboard";
import Interview from "@/pages/interview";
import NotFound from "@/pages/not-found";

function Router() {
  const { isAuthenticated, isLoading } = useAuth();

  return (
    <Switch>
      {isLoading || !isAuthenticated ? (
        <>
          <Route path="/" component={Landing} />
          <Route component={NotFound} />
        </>
      ) : (
        <>
          <Route path="/" component={Home} />
          <Route path="/dashboard" component={Dashboard} />
          <Route path="/interview/:id" component={Interview} />
          <Route component={NotFound} />
        </>
      )}
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

