import { GoogleGenAI } from "@google/genai";

const ai = new GoogleGenAI({ 
  apiKey: process.env.GEMINI_API_KEY || process.env.GOOGLE_GEMINI_API_KEY || "" 
});

export interface GenerateQuestionsRequest {
  jobRole: string;
  experienceLevel: string;
  techStack: string[];
  questionCount: number;
}

export interface InterviewQuestion {
  question: string;
  type: 'technical' | 'behavioral' | 'situational';
  expectedAreas: string[];
}

export interface EvaluateResponseRequest {
  question: string;
  response: string;
  jobRole: string;
  experienceLevel: string;
}

export interface ResponseEvaluation {
  score: number; // 0-10
  feedback: string;
  strengths: string[];
  improvements: string[];
  confidenceLevel: number; // 0-100
  communicationScore: number; // 0-10
  technicalScore: number; // 0-10
}

export class GeminiService {
  async generateInterviewQuestions(request: GenerateQuestionsRequest): Promise<InterviewQuestion[]> {
    try {
      const systemPrompt = `You are an expert technical interviewer. Generate ${request.questionCount} interview questions for a ${request.experienceLevel} ${request.jobRole} position.

Tech stack: ${request.techStack.join(', ')}

Create a balanced mix of:
- 60% Technical questions (coding, system design, specific technologies)
- 25% Behavioral questions (teamwork, leadership, problem-solving)
- 15% Situational questions (handling conflicts, deadlines, challenges)

For each question, specify:
- The exact question text
- Question type (technical/behavioral/situational)
- Key areas the response should cover

Respond with valid JSON array format.`;

      const response = await ai.models.generateContent({
        model: "gemini-2.5-pro",
        config: {
          systemInstruction: systemPrompt,
          responseMimeType: "application/json",
          responseSchema: {
            type: "array",
            items: {
              type: "object",
              properties: {
                question: { type: "string" },
                type: { type: "string", enum: ["technical", "behavioral", "situational"] },
                expectedAreas: { type: "array", items: { type: "string" } }
              },
              required: ["question", "type", "expectedAreas"]
            }
          }
        },
        contents: `Generate interview questions for: ${request.jobRole} (${request.experienceLevel}) with tech stack: ${request.techStack.join(', ')}`
      });

      const rawJson = response.text;
      if (!rawJson) {
        throw new Error("Empty response from Gemini");
      }

      return JSON.parse(rawJson);
    } catch (error) {
      console.error("Error generating questions:", error);
      throw new Error(`Failed to generate interview questions: ${error}`);
    }
  }

  async evaluateResponse(request: EvaluateResponseRequest): Promise<ResponseEvaluation> {
    try {
      const systemPrompt = `You are an expert interview evaluator. Analyze the candidate's response and provide structured feedback.

Evaluate based on:
1. Technical accuracy and depth (for technical questions)
2. Communication clarity and structure
3. Confidence and presentation
4. Completeness of the answer
5. Practical experience demonstration

Provide scores:
- Overall score: 0-10 (10 being excellent)
- Communication score: 0-10 (clarity, structure, articulation)
- Technical score: 0-10 (accuracy, depth, practical knowledge)
- Confidence level: 0-100 (body language, tone, conviction)

Give specific, actionable feedback including strengths and areas for improvement.

Respond with valid JSON format.`;

      const response = await ai.models.generateContent({
        model: "gemini-2.5-pro",
        config: {
          systemInstruction: systemPrompt,
          responseMimeType: "application/json",
          responseSchema: {
            type: "object",
            properties: {
              score: { type: "number" },
              feedback: { type: "string" },
              strengths: { type: "array", items: { type: "string" } },
              improvements: { type: "array", items: { type: "string" } },
              confidenceLevel: { type: "number" },
              communicationScore: { type: "number" },
              technicalScore: { type: "number" }
            },
            required: ["score", "feedback", "strengths", "improvements", "confidenceLevel", "communicationScore", "technicalScore"]
          }
        },
        contents: `
Position: ${request.jobRole} (${request.experienceLevel})
Question: ${request.question}
Candidate Response: ${request.response}

Evaluate this response comprehensively.`
      });

      const rawJson = response.text;
      if (!rawJson) {
        throw new Error("Empty response from Gemini");
      }

      return JSON.parse(rawJson);
    } catch (error) {
      console.error("Error evaluating response:", error);
      throw new Error(`Failed to evaluate response: ${error}`);
    }
  }

  async generateOverallFeedback(responses: ResponseEvaluation[]): Promise<string> {
    try {
      const systemPrompt = `You are an expert career coach. Based on the individual question evaluations, provide comprehensive interview feedback.

Include:
1. Overall performance summary
2. Key strengths demonstrated
3. Primary areas for improvement
4. Specific actionable recommendations
5. Next steps for skill development

Make it encouraging but honest, with concrete advice.`;

      const evaluationSummary = responses.map((r, i) => 
        `Question ${i + 1}: Score ${r.score}/10, Confidence ${r.confidenceLevel}%`
      ).join('\n');

      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        config: {
          systemInstruction: systemPrompt,
        },
        contents: `Based on these interview question evaluations:\n\n${evaluationSummary}\n\nProvide comprehensive feedback for the candidate.`
      });

      return response.text || "Unable to generate overall feedback";
    } catch (error) {
      console.error("Error generating overall feedback:", error);
      throw new Error(`Failed to generate overall feedback: ${error}`);
    }
  }
}

export const geminiService = new GeminiService();

server/services/livekit.ts.content:
import { AccessToken, RoomServiceClient } from 'livekit-server-sdk';

const apiKey = process.env.LIVEKIT_API_KEY || '';
const apiSecret = process.env.LIVEKIT_API_SECRET || '';
const wsUrl = process.env.LIVEKIT_URL || 'wss://your-livekit-instance.livekit.cloud';

export class LiveKitService {
  private roomService: RoomServiceClient;

  constructor() {
    this.roomService = new RoomServiceClient(wsUrl, apiKey, apiSecret);
  }

  async createInterviewRoom(interviewId: string): Promise<string> {
    try {
      const roomName = `interview-${interviewId}`;

      // Create room if it doesn't exist
      await this.roomService.createRoom({
        name: roomName,
        emptyTimeout: 300, // 5 minutes
        maxParticipants: 2, // User + AI
      });

      return roomName;
    } catch (error) {
      console.error('Error creating interview room:', error);
      throw new Error(`Failed to create interview room: ${error}`);
    }
  }

  async generateAccessToken(roomName: string, participantName: string, userId: string): Promise<string> {
    try {
      const at = new AccessToken(apiKey, apiSecret, {
        identity: userId,
        name: participantName,
      });

      at.addGrant({
        roomJoin: true,
        room: roomName,
        canPublish: true,
        canSubscribe: true,
        canPublishData: true,
      });

      return await at.toJwt();
    } catch (error) {
      console.error('Error generating access token:', error);
      throw new Error(`Failed to generate access token: ${error}`);
    }
  }

  async endInterviewRoom(roomName: string): Promise<void> {
    try {
      await this.roomService.deleteRoom(roomName);
    } catch (error) {
      console.error('Error ending interview room:', error);
      // Don't throw here as the interview might still be valid
    }
  }

  async getRoomInfo(roomName: string) {
    try {
      return await this.roomService.listRooms([roomName]);
    } catch (error) {
      console.error('Error getting room info:', error);
      return null;
    }
  }
}

export const liveKitService = new LiveKitService();

client/src/components/interview-feedback.content:
import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Navigation } from "@/components/navigation";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { isUnauthorizedError } from "@/lib/authUtils";
import { 
  ArrowLeft,
  Award,
  BarChart3,
  TrendingUp,
  MessageCircle,
  Code,
  Clock,
  CheckCircle2,
  Target,
  Star,
  Download,
  Share2
} from "lucide-react";

interface InterviewFeedbackProps {
  interviewId: string;
}

export function InterviewFeedback({ interviewId }: InterviewFeedbackProps) {
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();
  const { toast } = useToast();

  const { data: interview, isLoading, error } = useQuery({
    queryKey: ["/api/interviews", interviewId],
    retry: false,
    enabled: !!interviewId,
  });

  // Auth check
  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, authLoading, toast]);

  if (authLoading || !isAuthenticated) {
    return <div className="min-h-screen bg-slate-50 dark:bg-slate-900" />;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50 dark:bg-slate-900">
        <Navigation />
        <div className="pt-16 flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-slate-600 dark:text-slate-300">Loading your feedback...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error || !interview) {
    return (
      <div className="min-h-screen bg-slate-50 dark:bg-slate-900">
        <Navigation />
        <div className="pt-16 flex items-center justify-center min-h-screen">
          <Card className="w-full max-w-md mx-4">
            <CardContent className="pt-6 text-center">
              <h1 className="text-2xl font-bold text-slate-900 dark:text-white mb-4">
                Feedback Not Available
              </h1>
              <p className="text-slate-600 dark:text-slate-300 mb-6">
                Unable to load interview feedback. Please try again.
              </p>
              <Button asChild data-testid="button-back-dashboard">
                <Link href="/dashboard">
                  <ArrowLeft className="mr-2 h-4 w-4" />
                  Back to Dashboard
                </Link>
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const overallScore = interview.overallScore ? parseFloat(interview.overallScore) : 0;
  const communicationScore = interview.communicationScore ? parseFloat(interview.communicationScore) : 0;
  const technicalScore = interview.technicalScore ? parseFloat(interview.technicalScore) : 0;
  const confidenceLevel = interview.confidenceLevel ? parseFloat(interview.confidenceLevel) : 0;

  const getScoreColor = (score: number) => {
    if (score >= 8) return 'text-green-600 dark:text-green-400';
    if (score >= 6) return 'text-yellow-600 dark:text-yellow-400';
    return 'text-red-600 dark:text-red-400';
  };

  const getScoreLevel = (score: number) => {
    if (score >= 9) return 'Excellent';
    if (score >= 8) return 'Very Good';
    if (score >= 7) return 'Good';
    if (score >= 6) return 'Above Average';
    if (score >= 5) return 'Average';
    return 'Needs Improvement';
  };

  const answeredQuestions = interview.questions?.filter((q: any) => q.userResponse) || [];
  const completionRate = interview.questions?.length 
    ? (answeredQuestions.length / interview.questions.length) * 100 
    : 0;

  return (
    <div className="min-h-screen bg-slate-50 dark:bg-slate-900">
      <Navigation />

      <div className="pt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Header */}
          <div className="flex items-center justify-between mb-8">
            <div>
              <h1 className="text-3xl font-bold text-slate-900 dark:text-white mb-2">
                Interview Feedback
              </h1>
              <p className="text-lg text-slate-600 dark:text-slate-300">
                {interview.title}
              </p>
            </div>
            <div className="flex gap-3">
              <Button variant="outline" size="sm" data-testid="button-download-feedback">
                <Download className="mr-2 h-4 w-4" />
                Download Report
              </Button>
              <Button variant="outline" size="sm" data-testid="button-share-feedback">
                <Share2 className="mr-2 h-4 w-4" />
                Share
              </Button>
              <Button asChild data-testid="button-new-interview">
                <Link href="/">
                  Start New Interview
                </Link>
              </Button>
            </div>
          </div>

          {/* Celebration Header */}
          <Card className="mb-8 bg-gradient-to-r from-primary/10 to-secondary/10 border-primary/20">
            <CardContent className="p-8 text-center">
              <div className="w-20 h-20 bg-gradient-to-br from-primary to-secondary rounded-full mx-auto mb-6 flex items-center justify-center">
                <CheckCircle2 className="text-white text-3xl" />
              </div>
              <h2 className="text-2xl font-bold text-slate-900 dark:text-white mb-2">
                ðŸŽ‰ Great Job Completing Your Interview!
              </h2>
              <p className="text-slate-600 dark:text-slate-300 text-lg">
                You've taken an important step in your career journey. Here's your detailed feedback.
              </p>
            </CardContent>
          </Card>

          {/* Overall Score Overview */}
          <div className="grid md:grid-cols-4 gap-6 mb-8">
            <Card>
              <CardContent className="p-6 text-center">
                <div className="w-16 h-16 bg-gradient-to-br from-primary to-secondary rounded-full mx-auto mb-4 flex items-center justify-center">
                  <Award className="text-white text-2xl" />
                </div>
                <div className={`text-4xl font-bold mb-2 ${getScoreColor(overallScore)}`} data-testid="overall-score">
                  {overallScore.toFixed(1)}
                </div>
                <div className="text-sm text-slate-600 dark:text-slate-400">Overall Score</div>
                <div className="text-xs text-slate-500 dark:text-slate-500 mt-1">
                  {getScoreLevel(overallScore)}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6 text-center">
                <div className="w-16 h-16 bg-blue-500 rounded-full mx-auto mb-4 flex items-center justify-center">
                  <MessageCircle className="text-white text-xl" />
                </div>
                <div className={`text-4xl font-bold mb-2 ${getScoreColor(communicationScore)}`} data-testid="communication-score">
                  {communicationScore.toFixed(1)}
                </div>
                <div className="text-sm text-slate-600 dark:text-slate-400">Communication</div>
                <Progress value={communicationScore * 10} className="h-2 mt-2" />
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6 text-center">
                <div className="w-16 h-16 bg-green-500 rounded-full mx-auto mb-4 flex items-center justify-center">
                  <Code className="text-white text-xl" />
                </div>
                <div className={`text-4xl font-bold mb-2 ${getScoreColor(technicalScore)}`} data-testid="technical-score">
                  {technicalScore.toFixed(1)}
                </div>
                <div className="text-sm text-slate-600 dark:text-slate-400">Technical Skills</div>
                <Progress value={technicalScore * 10} className="h-2 mt-2" />
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6 text-center">
                <div className="w-16 h-16 bg-purple-500 rounded-full mx-auto mb-4 flex items-center justify-center">
                  <TrendingUp className="text-white text-xl" />
                </div>
                <div className="text-4xl font-bold text-purple-600 dark:text-purple-400 mb-2" data-testid="confidence-level">
                  {confidenceLevel.toFixed(0)}%
                </div>
                <div className="text-sm text-slate-600 dark:text-slate-400">Confidence</div>
                <Progress value={confidenceLevel} className="h-2 mt-2" />
              </CardContent>
            </Card>
          </div>

          <div className="grid lg:grid-cols-3 gap-8">
            {/* Detailed Feedback */}
            <div className="lg:col-span-2 space-y-6">
              {/* Overall Feedback */}
              {interview.feedback && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <BarChart3 className="h-5 w-5" />
                      Overall Performance Analysis
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="prose prose-slate dark:prose-invert max-w-none">
                      <p className="text-slate-700 dark:text-slate-300 leading-relaxed whitespace-pre-wrap">
                        {interview.feedback}
                      </p>
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Question by Question Breakdown */}
              <Card>
                <CardHeader>
                  <CardTitle>Question Analysis</CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  {answeredQuestions.map((question: any, index: number) => (
                    <div key={question.id} className="border-l-4 border-primary/30 pl-6">
                      <div className="flex items-start justify-between mb-3">
                        <div className="flex-1">
                          <h4 className="font-medium text-slate-900 dark:text-white mb-2">
                            Question {index + 1}
                          </h4>
                          <p className="text-slate-600 dark:text-slate-400 text-sm mb-3">
                            {question.questionText}
                          </p>
                          <Badge variant="outline" size="sm">
                            {question.questionType}
                          </Badge>
                        </div>
                        {question.responseScore && (
                          <div className="text-right ml-4">
                            <div className={`text-2xl font-bold ${getScoreColor(parseFloat(question.responseScore))}`}>
                              {parseFloat(question.responseScore).toFixed(1)}
                            </div>
                            <div className="text-xs text-slate-500">Score</div>
                          </div>
                        )}
                      </div>

                      {question.userResponse && (
                        <div className="bg-slate-50 dark:bg-slate-800 rounded-lg p-4 mb-3">
                          <h5 className="font-medium text-slate-900 dark:text-white mb-2">Your Response:</h5>
                          <p className="text-slate-700 dark:text-slate-300 text-sm">
                            {question.userResponse}
                          </p>
                        </div>
                      )}

                      {question.responseFeedback && (
                        <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-4">
                          <h5 className="font-medium text-blue-900 dark:text-blue-300 mb-2">AI Feedback:</h5>
                          <p className="text-blue-800 dark:text-blue-200 text-sm">
                            {question.responseFeedback}
                          </p>
                        </div>
                      )}

                      {index < answeredQuestions.length - 1 && (
                        <Separator className="mt-6" />
                      )}
                    </div>
                  ))}
                </CardContent>
              </Card>
            </div>

            {/* Interview Statistics and Next Steps */}
            <div className="space-y-6">
              {/* Interview Statistics */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <BarChart3 className="h-5 w-5" />
                    Interview Statistics
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-slate-600 dark:text-slate-400">Completion Rate</span>
                    <div className="text-right">
                      <span className="font-medium">{completionRate.toFixed(0)}%</span>
                      <Progress value={completionRate} className="h-2 w-20 mt-1" />
                    </div>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-slate-600 dark:text-slate-400">Questions Answered</span>
                    <span className="font-medium">
